data = FileAttachment("trends.csv").csv({ typed: true });

viewof selectedQuery = Inputs.select(d3.group(data, d =&gt; d.query), {label:"Query"})

Plot.plot({
    marks: [
        Plot.ruleY([0]),
        Plot.lineY(selectedQuery, {
            x: "week",
            y: "interest",
            curve: "linear",
            stroke: "query",
        }),
        Plot.crosshair(data, {
            x: "week",
            y: "interest"
        })
    ]
});

